pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./EzatERC20.sol";
import "./EzbtERC20.sol";
import "./EzioERC20.sol";
import "./Treasury.sol";
//import "hardhat/console.sol";

contract EzPurchase is AccessControlEnumerable,ReentrancyGuard{

  bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");
  bytes32 public constant TREASURY_ROLE = keccak256("TREASURY_ROLE");

  //子基金类型
  enum TYPE{
    A,
    B
  }
  string internal constant INVALID_TOKEN = "EzPurchase: Invalid Token";
  string internal constant INSUFFICIENT_AMT = "EzPurchase: Insufficient Amount Available";
  string internal constant NOT_INVESTED_INVITATION = "EzPurchase: Invitation Is Not Invest";
  string internal constant INVALID_INVITATION = "EzPurchase: Invalid Invitation";
  string internal constant WRONG_PARAMETER = "EzPurchase: Wrong Parameter";

  using SafeERC20 for IERC20;
  //稳定币合约
  IERC20 immutable usdt;
  //优先份额合约
  EzatERC20 immutable aToken;
  //进取份额合约
  EzbtERC20 immutable bToken;
  //治理Token
  EzioERC20 immutable cToken;
  //金库合约
  Treasury immutable treasury;

  //申购事件
  event Purchase(address indexed account, TYPE indexed type_, uint256 indexed amt_, uint256 qty_);
  //赎回事件
  event Redeem(address indexed account, TYPE indexed type_, uint256 indexed qty_, uint256 amt_);

  constructor(address usdt_,address aToken_,address bToken_,address cToken_,address payable treasury_){
    usdt = IERC20(usdt_);
    aToken = EzatERC20(aToken_);
    bToken = EzbtERC20(bToken_);
    cToken = EzioERC20(cToken_);
    treasury = Treasury(treasury_);
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(GOVERNOR_ROLE, msg.sender);
    _grantRole(GOVERNOR_ROLE, treasury_);
    _grantRole(TREASURY_ROLE, treasury_);
  }

  /**
  * @notice              投资人购买aToken或bToken
  * @param type_         0为aToken,1为bToken
  * @param amt_          购买花费的USDT
  */
  function purchase(TYPE type_,uint256 amt_) external nonReentrant(){
    require(amt_>0,WRONG_PARAMETER);
    //投资者将USDT发送到金库
    usdt.safeTransferFrom(msg.sender,address(treasury), amt_);
    if(type_ == TYPE.A){
      //console.log("start purchase aToken");
      //金库开采aToken给投资者
      uint256 qty = amt_* 1e18 / aToken.netWorth();
      treasury.mint(0,msg.sender, qty);
      emit Purchase(msg.sender,type_,amt_,qty);
      //aToken资金池增加
      treasury.incPooledAmount(0, amt_);
    }else if(type_ == TYPE.B){
      //console.log("start purchase bToken");
      //金库开采bToken给投资者
      uint256 qty = amt_* 1e18 / bToken.netWorth();
      treasury.mint(1,msg.sender,qty);
      emit Purchase(msg.sender,type_,amt_,qty);
      //bToken资金池增加
      treasury.incPooledAmount(1, amt_);
    }else{
      revert(INVALID_TOKEN);
    }
  }

  /**
  * @notice              投资人赎回aToken或bToken
  * @param type_         0为aToken,1为bToken
  * @param qty_          赎回的数量
  */
  function redeem(TYPE type_,uint256 qty_) external nonReentrant(){
    require(qty_>0,WRONG_PARAMETER);
    if(type_ == TYPE.A){
      //金库转USDT给用户
      uint256 amt = qty_ * aToken.netWorth() / 1e18;
      treasury.transfer(msg.sender, amt);
      //销毁aToken
      treasury.burn(0,msg.sender,qty_);
      //赎回时金库的操作
      treasury.redeem(0,amt);
      emit Redeem(msg.sender,type_,qty_,amt);
    }else if(type_ == TYPE.B){
      //金库转USDT给用户
      uint256 amt = qty_ * bToken.netWorth() / 1e18;
      treasury.transfer(msg.sender, amt);
      //销毁bToken
      treasury.burn(1,msg.sender,qty_);
      //赎回时金库的操作
      treasury.redeem(1,amt);
      emit Redeem(msg.sender,type_,qty_,amt);
    }else{
      revert(INVALID_TOKEN);
    }
  }

}
","keccak256":"0x4ab569a9f2e45a7582461daf582019043813a718886b0c41c138c4fcf4641269","license":"MIT"},"contracts/EzToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

contract EzToken is ERC20Pausable, AccessControlEnumerable {
  //MINTER_ROLE的hash
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
  //BURNER_ROLE的hash
  bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
  //TREASURY_ROLE的hash
  bytes32 public constant TREASURY_ROLE = keccak256("TREASURY_ROLE");

  constructor(string memory name_,string memory symbol_,uint256 initialSupply_) ERC20(name_,symbol_){
    _mint(msg.sender, initialSupply_*1e18);
    //赋予合约发布者管理员的权限
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  /**
  * @notice          开采token,onlyRole(MINTER_ROLE)修饰符确保只有合约初始化时设定的minter账户才能执行mint方法
  * @param to        获得token的账户
  * @param amount    挖矿的数量
  */
  function mint(address to, uint256 amount) public virtual onlyRole(MINTER_ROLE){
    _mint(to,amount);
  }

  /**
  * @notice          销毁token,onlyRole(BURNER_ROLE)修饰符确保只有合约初始化时设定的burner账户才能执行burn方法
  * @param from      销毁token的账户
  * @param amount    销毁的数量
  */
  function burn(address from, uint256 amount) public virtual onlyRole(BURNER_ROLE) {
    _burn(from,amount);
  }

  /**
  * @notice          暂停转账功能
  */
  function pause() external onlyRole(DEFAULT_ADMIN_ROLE){
    _pause();
  }

  /**
  * @notice             恢复转账功能
  */
  function unpause() external onlyRole(DEFAULT_ADMIN_ROLE){
    _unpause();
  }

}
