","keccak256":"0xbe81e0c69c3b8ae2231ca8db6a394010a55fb01a40664a0d0add315cab3efd74","license":"MIT"},"contracts/Swap.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
//import "hardhat/console.sol";

contract Swap is AccessControlEnumerable{

  bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");
  using SafeERC20 for IERC20;
  IERC20 immutable usdt;
  IERC20 immutable stEth;
  //chainlink聚合器
  AggregatorV3Interface internal priceFeed;

  constructor(address usdt_,address stEth_){
    usdt = IERC20(usdt_);
    stEth = IERC20(stEth_);
    priceFeed = AggregatorV3Interface(0xD4a33860578De61DBAbDc8BFdb98FD742fA7028e);
    _grantRole(GOVERNOR_ROLE, msg.sender);
  }

  receive() external payable{
  }

  //从chainlink获取最新的以太币价格
  function price() public view returns (uint256 _price){
    (,int256 answer,,,) = priceFeed.latestRoundData();
    //返回的answer是8位小数,转换为18位小数
    _price = uint256(answer)*1e10;
  }

  /**
  * @notice             测试环境模拟使用稳定币购买stEth
  * @param amount_      用于购买stEth的USDT的数量
  * @return uint256     购买的stEth的数量,单位是wei
  */
  function buyEther(uint256 amount_) external returns(uint256){
    uint256 ethAmount = amount_* 1 ether/price();
    //msg.sender发送USDT到交易所合约
    usdt.safeTransferFrom(msg.sender,address(this), amount_);
    //交易所发送stETH给msg.sender
    stEth.safeTransfer(msg.sender,ethAmount);
    return ethAmount;
  }

  /**
  * @notice               测试环境模拟使用stEth兑换USDT
  * @param ethAmount      出售的stEth的数量
  * @return uint256       购买的USDT的数量
  */
  function saleEther(uint256 ethAmount) external returns(uint256){
    //console.log("Swap.saleEther: msg.value=",msg.value);
    require(ethAmount>0,"Zero Ethereum Amount");
    uint256 usdtAmount = ethAmount * price() / 1 ether;
    //交易所发送USDT给msg.sender
    usdt.safeTransfer(msg.sender,usdtAmount);
    //msg.sender发送stETH到交易所合约
    stEth.safeTransferFrom(msg.sender,address(this), ethAmount);
    return usdtAmount;
  }

}
","keccak256":"0x74ab9dee245de88d1cc54e70c3e8355385fd10dd04b258a2955c84423922ce64","license":"MIT"},"contracts/Treasury.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./EzatERC20.sol";
import "./EzbtERC20.sol";
import "./EzioERC20.sol";
import "./EzPurchase.sol";
import "./Swap.sol";
//import "hardhat/console.sol";


contract Treasury is AccessControlEnumerable, ReentrancyGuard{
  bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");
  bytes32 public constant PURCHASE_ROLE = keccak256("PURCHASE_ROLE");

  using SafeERC20 for IERC20;
  //稳定币合约
  IERC20 immutable usdt;
  //交易所合约
  Swap immutable swap;
  //优先份额合约
  EzatERC20 immutable aToken;
  //进取份额合约
  EzbtERC20 immutable bToken;
  //奖励Token合约
  EzioERC20 immutable cToken;
  //stEth
  IERC20 immutable stEth;
  //申购合约
  EzPurchase purchase;
  //是否初始化
  bool private initialized;
  //以太币总储量,购买stEth时会增加,出售stEth时会减少
  uint256 public totalReserve;
  //未配对的aToken资金
  uint256 public pooledA;
  //已配对的aToken资金
  uint256 public matchedA;
  //未配对的bToken资金
  uint256 public pooledB;
  //回报率
  uint256 public immutable rewardRate;
  //回报率的分母
  uint256 public constant REWARD_RATE_DENOMINATOR = 100000000;
  //杠杆倍数的分母
  uint256 public constant LEVERAGE_DENOMINATOR = 10000;

  string internal constant INVALID_TOKEN = "Treasury: Invalid Token";
  string internal constant ALREADY_INITIALIZED = "Contract Already Initialized";

  //投资事件
  event Invest(uint256 indexed amountA,uint256 indexed amountB);
  //购买以太币事件
  event Buy(uint256 indexed amt, uint256 indexed ethQty,uint256 indexed totalReserve);
  //出售以太币事件
  event Sale(uint256 indexed ethQty, uint256 indexed amt,uint256 indexed totalReserve);
  //转账USDT事件
  event Transfer(address indexed to, uint256 indexed qty);

  constructor(address usdt_,address aToken_,address bToken_,address cToken_,address stEth_,address payable swap_,uint256 rewardRate_){
    usdt = IERC20(usdt_);
    aToken = EzatERC20(aToken_);
    bToken = EzbtERC20(bToken_);
    cToken = EzioERC20(cToken_);
    stEth = IERC20(stEth_);
    swap = Swap(swap_);
    rewardRate = rewardRate_;
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(GOVERNOR_ROLE, msg.sender);
  }

  receive() external payable{
  }

  /**
  * @notice           初始化方法
  * @param purchase_  申购合约地址
  */
  function initialize(address purchase_) external onlyRole(DEFAULT_ADMIN_ROLE){
    require(!initialized, ALREADY_INITIALIZED);
    initialized = true;
    purchase = EzPurchase(purchase_);
    _grantRole(PURCHASE_ROLE, purchase_);
  }

  /**
  * @notice              开采aToken和bToken
  * @param type_         0为aToken,1为bToken
  * @param to_           账户地址
  * @param qty_          数量
  */
  function mint(uint8 type_,address to_,uint256 qty_) external onlyRole(PURCHASE_ROLE){
    if(type_ == 0){
      //开采aToken
      aToken.mint(to_,qty_);
    }else if(type_ == 1){
      //开采bToken
      bToken.mint(to_,qty_);
    }else{
      revert(INVALID_TOKEN);
    }
  }

  /**
  * @notice              销毁aToken和bToken
  * @param type_         0为aToken,1为bToken
  * @param from_         账户地址
  * @param qty_          数量
  */
  function burn(uint8 type_,address from_,uint256 qty_) external onlyRole(PURCHASE_ROLE){
    if(type_ == 0){
      //销毁aToken
      aToken.burn(from_,qty_);
    }else if(type_ == 1){
      //销毁bToken
      bToken.burn(from_,qty_);
    }else{
      revert(INVALID_TOKEN);
    }
  }

  /**
  * @notice              增加未配对资金
  * @param type_         0为aToken,1为bToken
  * @param amt_          金额
  */
  function incPooledAmount(uint8 type_,uint256 amt_) public onlyRole(PURCHASE_ROLE){
    if(type_ == 0){
      //pooledA增加
      pooledA += amt_;
    }else if(type_ == 1){
      //pooledB增加
      pooledB += amt_;
    }else{
      revert(INVALID_TOKEN);
    }
  }

  /**
  * @notice              减少未配对资金
  * @param type_         0为aToken,1为bToken
  * @param amt_          金额
  */
  function decPooledAmount(uint8 type_,uint256 amt_) internal{
    if(type_ == 0){
      //pooledA减少
      pooledA -= amt_;
    }else if(type_ == 1){
      //pooledB减少
      pooledB -= amt_;
    }else{
      revert(INVALID_TOKEN);
    }
  }

  /**
  * @notice              增加aToken已配对资金
  * @param amt_          金额
  */
  function incMatchedA(uint256 amt_) internal{
      matchedA += amt_;
  }

  /**
  * @notice              减少已配对aToken资金
  * @param amt_          金额
  */
  function decMatchedA(uint256 amt_) internal{
    matchedA -= amt_;
  }

  /**
  * @notice              计算bToken的已配对资金
  * @return uint256
  */
  function matchedB() public view returns(uint256){
    return totalReserve*ethPrice()/1e18-matchedA;
  }

  /**
  * @notice              金库发送USDT给用户
  * @param to_           账户地址
  * @param qty_          USDT数量
  */
  function transfer(address to_,uint256 qty_) external nonReentrant() onlyRole(PURCHASE_ROLE){
    usdt.safeTransfer(to_,qty_);
    emit Transfer(to_,qty_);
  }

  /**
  * @notice              从资金池中拿出资金进行投资
  */
  function invest() external onlyRole(GOVERNOR_ROLE) nonReentrant(){
    if(pooledB>0){
      //判断B资金池资金资金是否大于A资金池资金
      if(pooledB>pooledA){
        //如果大于则调用A、B资金池的全部资金
        _buyEth(pooledB+pooledA);
        incMatchedA(pooledA);
        emit Invest(pooledA,pooledB);
        decPooledAmount(0,pooledA);
        decPooledAmount(1,pooledB);
      }else{
        //如果小于或等于则调用B资金池资金和同等的A资金池资金
        _buyEth(pooledB*2);
        incMatchedA(pooledB);
        emit Invest(pooledB,pooledB);
        decPooledAmount(0,pooledB);
        decPooledAmount(1,pooledB);
      }
    }
  }

  /**
  * @notice              赎回时金库的操作
  * @param type_         0为aToken,1为bToken
  * @param amt_          金额
  */
  function redeem(uint8 type_,uint256 amt_) external onlyRole(PURCHASE_ROLE) nonReentrant(){
    if(type_ == 0){
      if(amt_ <= pooledA){
        //pooledA减少
        decPooledAmount(0,amt_);
      }else{
        //出售stETH
        uint256 saleWorth = amt_ - pooledA;
        uint256 stEthQty = saleWorth * 1e18 / ethPrice();
        _saleEth(stEthQty);
        //pooledA减少,matchedA减少
        decMatchedA(saleWorth);
        decPooledAmount(0,pooledA);
      }
    }else if(type_ == 1){
      if(amt_ <= pooledB){
        //pooledB减少
        decPooledAmount(1,amt_);
      }else{
        //出售stETH
        uint256 stEthQty = totalReserve * amt_ / bToken.totalNetWorth();
        uint usdtAmount = _saleEth(stEthQty);
        //pooledA增加
        incPooledAmount(0,usdtAmount-amt_);
        //matchedA减少
        decMatchedA(usdtAmount-amt_);
      }
    }else{
      revert(INVALID_TOKEN);
    }
  }

  /**
  * @notice           模拟用USDT向交易所购买stEth,非安全方法
  * @param amt_       用于购买以太币的USDT
  * @return uint256   购买的stEth数量,单位是wei
  */
  function _buyEth(uint256 amt_) internal returns (uint256){
    require(usdt.approve(address(swap),amt_),"Treasury:Approve Failed");
    //console.log("Treasury._buyEth: allowance balance=",usdt.allowance(address(this),address(swap)));
    //console.log("Treasury._buyEth: treasury balance=",usdt.balanceOf(address(this)));
    uint256 ethQty = swap.buyEther(amt_);
    require(ethQty > 0,"Buy Ether Error");
    totalReserve += ethQty;
    emit Buy(amt_,ethQty,totalReserve);
    return ethQty;
  }

  /**
  * @notice         模拟向交易所出售以太币,非安全方法
  * @param qty_     出售的stEth数量,单位为wei
  * @return uint256    换取的USDT的数量
  */
  function _saleEth(uint256 qty_) internal returns (uint256){
    require(qty_>0,"Treasury:Wrong Parameter");
    require(stEth.approve(address(swap),qty_),"Treasury:Approve Failed");
    //总储量减少
    totalReserve -= qty_;
    uint256 usdtAmount = swap.saleEther(qty_);
    emit Sale(qty_,usdtAmount,totalReserve);
    return usdtAmount;
  }

  /**
  * @notice    检查bToken的已配对资金,如果小于aToken的已配对资金的60%则触发下折
  */
  function check() external nonReentrant() {
    //如果stETH下跌导致bToken归零,则让matchedA等于总储备净值
    if(totalReserve*ethPrice()/1e18<matchedA){
      matchedA = totalReserve*ethPrice()/1e18;
    }
    if(matchedB()<matchedA*3/5){
      _convertDown();
    }
  }

  /**
  * @notice    下折,出售1/4的stETH,所获USDT存入pooledA
  */
  function _convertDown() internal {
    //console.log("Treasury._convertDown start");
    uint256 saleAmt = _saleEth(totalReserve/4);
    matchedA -= saleAmt;
    pooledA += saleAmt;
  }

  /**
  * @notice           杠杆率=aToken已配对资金/bToken已配对资金+1
  * @return uint256   bToken的杠杆率
  */
  function leverage() external view returns(uint256){
    return matchedB()>0?LEVERAGE_DENOMINATOR*matchedA/matchedB()+1*LEVERAGE_DENOMINATOR:20000;
  }

  /**
  * @notice               金库总储备净值=以太币总储量*当前以太币价格+pooledA+pooledB
  * @return uint256       金库总储备净值
  */
  function totalNetWorth() public view returns(uint256){
    return totalReserve*swap.price()/1e18+pooledA+pooledB;
  }

  /**
  * @notice               返回交易所的stEth价格
  * @return uint256
  */
  function ethPrice() public view returns(uint256){
    return swap.price();
  }

  /**
  * @notice               变基,aToken的已配对资金增加
  */
  function rebase() public onlyRole(GOVERNOR_ROLE){
    matchedA += matchedA * rewardRate/REWARD_RATE_DENOMINATOR;
  }

  /**
  * @notice               动态计算aToken的日利息
  */
  function interestRate() public view returns(uint256){
    return aToken.totalNetWorth()>0?(matchedA * rewardRate)/ aToken.totalNetWorth():rewardRate;
  }

}
