contracts/EzioV1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';
import '@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol';
import "./EzUSD.sol";
import "./EzMATIC.sol";
import "./EzioERC20.sol";
import "./Conversion.sol";
import "./SwapCollector.sol";
import "./interfaces/IEzio.sol";
import "hardhat/console.sol";

contract EzioV1 is AccessControlEnumerable,ReentrancyGuard,Conversion,SwapCollector,IEzio{

  bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");

  string internal constant INVALID_TOKEN = "Ezio: Invalid Token";
  string internal constant INSUFFICIENT_AMT = "Ezio: Insufficient Amount Available";
  string internal constant WRONG_PARAMETER = "Ezio: Wrong Parameter";
  string internal constant WRONG_BUY_TOKEN = "Ezio: Wrong Buy Token";
  string internal constant WRONG_SELL_TOKEN = "Ezio: Wrong Sell Token";
  string internal constant AMOUNT_MISMATCH = "Ezio: Amount Mismatch";

  using SafeERC20 for IERC20Metadata;
  //优先份额合约
  EzUSD immutable aToken;
  //进取份额合约
  EzMATIC immutable bToken;
  //治理Token
  EzioERC20 immutable cToken;
  //储备币总储量,购买储备币时会增加,出售储备币时会减少
  uint256 public totalReserve;
  //未配对的aToken资金
  uint256 public pooledA;
  //已配对的aToken资金
  uint256 public matchedA;
  //回报率
  uint16 public rewardRate;
  //回报率的分母
  uint256 public constant REWARD_RATE_DENOMINATOR = 10000000;
  //上次rebase的时间戳
  uint256 public lastRebaseTime;
  //杠杆倍数的分母
  uint256 public constant LEVERAGE_DENOMINATOR = 10000;
  //储备币存储门槛
  uint256 threshold = 10 * 1e18;
  //赎回的手续费费率
  uint16 public redeemFeeRate;
  //赎回手续费费率的分母
  uint256 public constant REDEEM_RATE_DENOMINATOR = 100;

  //申购事件
  event Purchase(address indexed account, TYPE indexed type_, uint256 indexed amt_, uint256 qty_);
  //赎回事件
  event Redeem(address indexed account, TYPE indexed type_, uint256 indexed qty_, uint256 amt_);
  //触发下折事件
  event CheckConvertDown(uint256 indexed matchedB, uint256 indexed totalNetWorth, uint256 time);

  constructor(address stableToken_,address reserveToken_,address aToken_,address bToken_,address cToken_,uint16 rewardRate_,uint16 redeemFeeRate_) Conversion(stableToken_,reserveToken_){
    aToken = EzUSD(aToken_);
    bToken = EzMATIC(bToken_);
    cToken = EzioERC20(cToken_);
    rewardRate = rewardRate_;
    lastRebaseTime = block.timestamp;
    redeemFeeRate = redeemFeeRate_;
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(GOVERNOR_ROLE, msg.sender);
  }


  /**
  * @notice              投资人购买aToken或bToken
  * @param type_         0为aToken,1为bToken
  * @param quotes_       请求参数
  */
  function purchase(TYPE type_,SwapQuote[] calldata quotes_) external nonReentrant(){
    require(quotes_.length==1||quotes_.length==2,WRONG_PARAMETER);
    SwapQuote calldata quote_ = quotes_[0];
    require(quote_.sellAmount>0,WRONG_PARAMETER);
    IERC20Metadata token = IERC20Metadata(quote_.sellToken);
    token.safeTransferFrom(msg.sender,address(this),quote_.sellAmount);
    uint256 stableAmount;
    if(type_==TYPE.A){
      //console.log("start purchase aToken");
      if(quote_.sellToken==STABLE_COIN_ADDRESS){
        stableAmount = quote_.sellAmount;
      }else{
        //跟交易所进行交易换成STABLE_COIN
        require(quote_.buyToken==STABLE_COIN_ADDRESS,WRONG_BUY_TOKEN);
        stableAmount = oneInchSwap(quote_,quote_.sellAmount);
      }
      //开采aToken给投资者
      uint256 qty = stableAmount * 1e18/ aToken.netWorth();
      //console.log("aToken qty=",qty);
      aToken.mint(msg.sender, qty);
      pooledA += stableAmount;
      emit Purchase(msg.sender,TYPE.A,stableAmount,qty);
    }else if(type_==TYPE.B){
      //console.log("start purchase bToken");
      require(quote_.buyToken==RESERVE_COIN_ADDRESS,WRONG_BUY_TOKEN);
      if(quote_.sellToken==STABLE_COIN_ADDRESS){
        stableAmount = quote_.sellAmount;
      }else{
        //转化为STABLE_COIN的数量
        stableAmount = convertAmt(quote_.sellToken,STABLE_COIN_ADDRESS,quote_.sellAmount);
      }
      //console.log("stableAmount=",stableAmount);
      //开采bToken给投资者
      uint256 qty = stableAmount * 1e18/ bToken.netWorth();
      //console.log("bToken qty=",qty);
      bToken.mint(msg.sender, qty);
      //使用用户资金跟交易所进行交易换成储备币,totalReserve增加
      uint256 reserveAmountB = oneInchSwap(quote_,quote_.sellAmount);
      totalReserve += reserveAmountB;
      emit Purchase(msg.sender,TYPE.B,stableAmount,qty);
    }else{
      revert(INVALID_TOKEN);
    }
    if(quotes_.length==2){
      require(type_==TYPE.B,WRONG_PARAMETER);
      SwapQuote calldata quoteExt_ = quotes_[1];
      require(quoteExt_.buyToken==RESERVE_COIN_ADDRESS,WRONG_BUY_TOKEN);
      require(quoteExt_.sellToken==STABLE_COIN_ADDRESS,WRONG_SELL_TOKEN);
      uint stableAmountExt;
      if(pooledA>stableAmount){
        stableAmountExt = stableAmount;
      }else{
        stableAmountExt = pooledA;
      }
      pooledA -= stableAmountExt;
      matchedA += stableAmountExt;
      //使用储备资金跟交易所进行交易换成储备币,totalReserve增加
      uint256 reserveAmountA = oneInchSwap(quoteExt_,stableAmountExt);
      totalReserve += reserveAmountA;
    }
  }

  /**
  * @notice              投资人赎回aToken或bToken
  * @param type_         0为aToken,1为bToken
  * @param qty_          赎回的数量
  * @param quote_        请求参数
  */
  function redeem(TYPE type_,uint256 qty_,SwapQuote calldata quote_) external nonReentrant(){
    require(qty_>0,WRONG_PARAMETER);
    if(type_ == TYPE.A){
      //金库转STABLE_COIN给用户
      uint256 amt = qty_ * aToken.netWorth() / 1e18 ;
      //赎回时金库的操作
      if(amt <= pooledA){
        //销毁aToken
        aToken.burn(msg.sender,qty_);
        stableToken.safeTransfer(msg.sender, amt * (REDEEM_RATE_DENOMINATOR - redeemFeeRate) / REDEEM_RATE_DENOMINATOR);
        pooledA -= amt;
      }else{
        //出售储备币换成稳定币再交易给用户
        uint256 saleAmount = amt - pooledA;
        uint256 saleQty = saleAmount * 1e18 / getPrice(RESERVE_COIN_ADDRESS);
        oneInchSwap(quote_,saleQty);
        //销毁aToken
        aToken.burn(msg.sender,qty_);
        stableToken.safeTransfer(msg.sender, amt * (REDEEM_RATE_DENOMINATOR - redeemFeeRate) / REDEEM_RATE_DENOMINATOR);
        totalReserve -= saleQty;
        matchedA -= saleAmount;
        pooledA -= pooledA;
      }
      emit Redeem(msg.sender,type_,qty_,amt);
    }else if(type_ == TYPE.B){
      //金库转STABLE_COIN给用户
      uint256 amt = qty_ * bToken.netWorth() / 1e18;
      //赎回时金库的操作
      uint256 saleAmount = totalNetWorth() * qty_ / bToken.totalSupply();
      uint256 saleQty = saleAmount * 1e18 / getPrice(RESERVE_COIN_ADDRESS);
      oneInchSwap(quote_,saleQty);
      //销毁bToken
      bToken.burn(msg.sender,qty_);
      stableToken.safeTransfer(msg.sender, amt * (REDEEM_RATE_DENOMINATOR - redeemFeeRate) / REDEEM_RATE_DENOMINATOR);
      totalReserve -= saleQty;
      pooledA += (saleAmount-amt);
      matchedA -= (saleAmount-amt);
      emit Redeem(msg.sender,type_,qty_,amt);
    }else{
      revert(INVALID_TOKEN);
    }
  }

  /**
  * @notice               金库总储备净值=储备币总储量*当前储备币价格+pooledA
  * @return uint256       金库总储备净值
  */
  function totalNetWorth() public view returns(uint256){
    return totalReserve*getPrice(RESERVE_COIN_ADDRESS)/1e18+pooledA;
  }

  /**
  * @notice               动态计算aToken的日利息
  */
  function interestRate() public view returns(uint256){
    return aToken.totalNetWorth()>0?(matchedA * rewardRate) / aToken.totalNetWorth():rewardRate;
  }

  /**
  * @notice           杠杆率=aToken已配对资金/bToken已配对资金+1
  * @return uint256   bToken的杠杆率
  */
  function leverage() external view returns(uint256){
    return bToken.totalNetWorth()>0?LEVERAGE_DENOMINATOR*matchedA/bToken.totalNetWorth()+1*LEVERAGE_DENOMINATOR:20000;
  }

  /**
  * @notice     设置储备币存储门槛
  */
  function setThreshold(uint256 threshold_) external onlyOwner{
    threshold = threshold_;
  }

  /**
  * @notice    检查bToken的已配对资金,如果小于aToken的已配对资金的60%则触发下折
  */
  function check() external nonReentrant() {
    //如果储备币下跌导致bToken归零,则让matchedA等于总储备净值
    if(totalNetWorth()<matchedA){
      matchedA = totalNetWorth();
    }
    if(bToken.totalNetWorth()<matchedA*3/5){
      //触发下折提示事件
      emit CheckConvertDown(bToken.totalNetWorth(),totalNetWorth(),block.timestamp);
    }
  }

  /**
  * @notice             下折操作,只能由管理员发起
  * @param quote_       请求参数
  */
  function convertDown(SwapQuote calldata quote_) external onlyRole(GOVERNOR_ROLE) {
    require(quote_.sellToken==RESERVE_COIN_ADDRESS,WRONG_SELL_TOKEN);
    require(quote_.buyToken==STABLE_COIN_ADDRESS,WRONG_BUY_TOKEN);
    require(totalReserve / 4  == quote_.sellAmount,WRONG_PARAMETER);
    uint256 buyAmount = oneInchSwap(quote_,quote_.sellAmount);
    //console.log("buyAmount=",buyAmount);
    totalReserve -= quote_.sellAmount;
    matchedA -= buyAmount;
    pooledA += buyAmount;
  }

  /**
  * @notice               变基,aToken的已配对资金增加
  */
  function rebase() external nonReentrant(){
    if(block.timestamp-lastRebaseTime >= 86400){
      matchedA += matchedA * rewardRate / REWARD_RATE_DENOMINATOR;
      lastRebaseTime += 86400;
    }
  }

}

contracts/EzMATIC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./EzToken.sol";
import "./interfaces/IEzio.sol";
//import "hardhat/console.sol";

contract EzMATIC is EzToken{
  //是否初始化
  bool private initialized;
  string internal constant ALREADY_INITIALIZED = "Contract Already Initialized";
  //Ezio主合约
  IEzio ezio;

  constructor() EzToken("Enterprise Token", "EzMATIC",0) {
  }

  /**
  * @notice           初始化方法
  * @param addr_      Ezio主合约地址
  */
  function initialize(address addr_) external onlyRole(DEFAULT_ADMIN_ROLE){
    require(!initialized, ALREADY_INITIALIZED);
    initialized = true;
    ezio = IEzio(addr_);
    _grantRole(MINTER_ROLE, addr_);
    _grantRole(BURNER_ROLE, addr_);
    _grantRole(GOVERNOR_ROLE, addr_);
  }

  /**
  * @notice          净值=总净值/总供应量
  * @return uint256  bToken的净值
  */
  function netWorth() public view returns(uint256){
    return totalSupply()==0?1e6:totalNetWorth()*1e18/totalSupply();
  }

  /**
  * @notice            总净值=金库总净值-aToken的总净值
  * @return uint256    bToken总净值
  */
  function totalNetWorth() public view virtual returns (uint256){
    return ezio.totalNetWorth()-ezio.matchedA()-ezio.pooledA();
  }

}

contracts/EzToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

contract EzToken is ERC20Pausable, AccessControlEnumerable {
  //MINTER_ROLE的hash
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
  //BURNER_ROLE的hash
  bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
  //GOVERNOR_ROLE的hash
  bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");

  constructor(string memory name_,string memory symbol_,uint256 initialSupply_) ERC20(name_,symbol_){
    _mint(msg.sender, initialSupply_*1e18);
    //赋予合约发布者超级管理员的权限
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  /**
  * @notice          开采token,onlyRole(MINTER_ROLE)修饰符确保只有合约初始化时设定的minter账户才能执行mint方法
  * @param to        获得token的账户
  * @param amount    挖矿的数量
  */
  function mint(address to, uint256 amount) public virtual onlyRole(MINTER_ROLE){
    _mint(to,amount);
  }

  /**
  * @notice          销毁token,onlyRole(BURNER_ROLE)修饰符确保只有合约初始化时设定的burner账户才能执行burn方法
  * @param from      销毁token的账户
  * @param amount    销毁的数量
  */
  function burn(address from, uint256 amount) public virtual onlyRole(BURNER_ROLE) {
    _burn(from,amount);
  }

  /**
  * @notice          暂停转账功能
  */
  function pause() external onlyRole(DEFAULT_ADMIN_ROLE){
    _pause();
  }

  /**
  * @notice             恢复转账功能
  */
  function unpause() external onlyRole(DEFAULT_ADMIN_ROLE){
    _unpause();
  }

}

