// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";

struct OneInchData {
  IERC20 sellToken;
  IERC20 buyToken;
  uint _sellAmt;
  uint _buyAmt;
  bytes callData;
}

contract OneInchSwap {
  address internal constant oneInchAddr = 0x1111111254EEB25477B68fb85Ed929f73A960582;
  address constant internal maticAddr = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
  using SafeERC20 for IERC20;

  receive() external payable{
  }

  function swap(address sellAddr,address buyAddr, uint256 sellAmt, bytes calldata callData) external payable returns (uint256) {
    IERC20 sellToken = IERC20(sellAddr);
    IERC20 buyToken = IERC20(buyAddr);
    uint ethAmt;
    if (address(sellToken) == maticAddr) {
      ethAmt = msg.value;
    } else {
      sellToken.safeTransferFrom(msg.sender,address(this),sellAmt);
      sellToken.safeApprove(oneInchAddr, sellAmt);
    }
    (bool success, bytes memory data) = oneInchAddr.call{value: ethAmt}(callData);
    if (!success) revert("1Inch-swap-failed");
    (uint256 buyAmt,) = abi.decode(data, (uint256, uint256));
    //console.log("buyAmt=",buyAmt);
    //console.log("gasLeft=",gasLeft);
    buyToken.safeTransfer(msg.sender,buyAmt);
    return buyAmt;
  }


}

contracts/SwapCollector.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';
import '@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol';
import "./interfaces/ISwapCollector.sol";
import "hardhat/console.sol";

contract SwapCollector is ISwapCollector{

  using SafeERC20 for IERC20Metadata;

  //0x交换路由地址
  address internal constant zeroExAddr = 0xDef1C0ded9bec7F1a1670819833240f027b25EfF;
  //1inch交换路由
  address internal constant oneInchAddr = 0x1111111254EEB25477B68fb85Ed929f73A960582;

  //uniswap交换路由
  ISwapRouter constant swapRouter = ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);
  //uniswap费率
  uint24 public constant feeTier = 3000;

  string internal constant CANNOT_BE_ZERO = "SwapCollector: Cannot be zero";

  /**
  * @notice               用0x进行交易
  * @param quote          请求参数
  * @param approveAmount  approve数量
  */
  function zeroExSwap(SwapQuote calldata quote,uint256 approveAmount) internal returns (uint256){
    IERC20Metadata(quote.sellToken).safeApprove(zeroExAddr, approveAmount);
    (bool success,bytes memory data) = zeroExAddr.call(quote.swapCallData);
    (uint256 buyAmount) = abi.decode(data,(uint256));
    require(success, '0x-swap-failed');
    return buyAmount;
  }

  /**
  * @notice               用1inch进行交易
  * @param quote          请求参数
  * @param approveAmount  approve数量
  */
  function oneInchSwap(SwapQuote calldata quote, uint256 approveAmount) internal returns (uint256) {
    IERC20Metadata(quote.sellToken).safeApprove(oneInchAddr, approveAmount);
    (bool success, bytes memory data) = oneInchAddr.call(quote.swapCallData);
    (uint256 buyAmount,) = abi.decode(data, (uint256, uint256));
    if (!success) revert("1Inch-swap-failed");
    return buyAmount;
  }

  /**
  * @notice          用uniswap进行交易
  * @param tokenIn_             消耗token的地址
  * @param tokenOut_            获得token的地址
  * @param amountIn_            消耗token的数量
  * @param amountOutMinimum_    获得token的最少数量
  */
  function uniSwap(address tokenIn_,address tokenOut_,uint256 amountIn_,uint256 amountOutMinimum_) internal returns (uint256){
    require(amountIn_>0,CANNOT_BE_ZERO);
    //uniswap交易
    TransferHelper.safeApprove(tokenIn_, address(swapRouter), amountIn_);
    ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
    tokenIn: tokenIn_,
    tokenOut: tokenOut_,
    fee: feeTier,
    recipient: address(this),
    deadline: block.timestamp,
    amountIn: amountIn_,
    amountOutMinimum: amountOutMinimum_,
    sqrtPriceLimitX96: 0
    });
    uint256 amountOut = swapRouter.exactInputSingle(params);
    return amountOut;
  }

}

