// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.0;

import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';
import '@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol';

contract Uniswap {
  ISwapRouter public immutable swapRouter;
  string internal constant WRONG_PARAMETER = "EzPurchase: Wrong Parameter";
  uint24 public constant feeTier = 3000;

  constructor(ISwapRouter _swapRouter) {
    swapRouter = _swapRouter;
  }

  function swap(address tokenIn_,address tokenOut_,uint256 amountIn_,uint256 amountOutMinimum_) external returns (uint256){
    require(amountIn_>0,WRONG_PARAMETER);
    //uniswap交易
    TransferHelper.safeApprove(tokenIn_, address(swapRouter), amountIn_);
    ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
    tokenIn: tokenIn_,
    tokenOut: tokenOut_,
    fee: feeTier,
    recipient: address(this),
    deadline: block.timestamp,
    amountIn: amountIn_,
    amountOutMinimum: amountOutMinimum_,
    sqrtPriceLimitX96: 0
    });
    //console.log("amountIn=",amountIn_);
    //console.log("amountOutMinimum=",amountOutMinimum_);
    uint256 amountOut = swapRouter.exactInputSingle(params);
    //console.log("amountOut=",amountOut);
    return amountOut;
  }
}

