// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "hardhat/console.sol";

// A partial WETH interfaec.
interface IWETH is IERC20 {
    function deposit() external payable;
}

// Demo contract that swaps its ERC20 balance for another ERC20.
// NOT to be used in production.
contract ZeroExSwap {
    event BoughtTokens(IERC20 sellToken, IERC20 buyToken, uint256 boughtAmount);

    using SafeERC20 for IERC20;

    struct QuoteResponse {
        IERC20 sellToken;
        IERC20 buyToken;
        address spender;
        address payable swapTarget;
        uint256 sellAmount;
        bytes swapCallData;
    }

    // The WETH contract.
    IWETH public immutable WETH;
    // Creator of this contract.
    address public owner;
    // 0x ExchangeProxy address.
    // See https://docs.0x.org/developer-resources/contract-addresses
    address public exchangeProxy;

    constructor(IWETH _weth, address _exchangeProxy) {
        WETH = _weth;
        exchangeProxy = _exchangeProxy;
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "ONLY_OWNER");
        _;
    }

    // Payable fallback to allow this contract to receive protocol fee refunds.
    receive() external payable {}

    // Transfer tokens held by this contrat to the sender/owner.
    function withdrawToken(IERC20 token, uint256 amount)
    external
    onlyOwner
    {
        require(token.transfer(msg.sender, amount));
    }

    // Transfer ETH held by this contrat to the sender/owner.
    function withdrawETH(uint256 amount)
    external
    onlyOwner
    {
        payable(msg.sender).transfer(amount);
    }

    // Transfer ETH into this contract and wrap it into WETH.
    function depositETH()
    external
    payable
    {
        WETH.deposit{value: msg.value}();
    }

    // Swaps ERC20->ERC20 tokens held by this contract using a 0x-API quote.
    function swap(
    // The `sellTokenAddress` field from the API response.
        IERC20 sellToken,
    // The `buyTokenAddress` field from the API response.
        IERC20 buyToken,
    // The `allowanceTarget` field from the API response.
        address spender,
    // The `to` field from the API response.
        address payable swapTarget,
    // The `sellAmount` field from the API response.
        uint256 sellAmount,
    // The `data` field from the API response.
        bytes calldata swapCallData
    )
    public
    onlyOwner
    payable // Must attach ETH equal to the `value` field from the API response.
    {
        // Checks that the swapTarget is actually the address of 0x ExchangeProxy
        require(swapTarget == exchangeProxy, "Target not ExchangeProxy");

        // Track our balance of the buyToken to determine how much we've bought.
        uint256 boughtAmount = buyToken.balanceOf(address(this));
        console.log("boughtAmount Before=",boughtAmount);
        console.log("sellTokenAmount Before=",sellToken.balanceOf(address(this)));
        // Give `spender` an infinite allowance to spend this contract's `sellToken`.
        // Note that for some tokens (e.g., USDT, KNC), you must first reset any existing
        // allowance to 0 before being able to update it.
        sellToken.safeApprove(spender, sellAmount);
        // Call the encoded swap function call on the contract at `swapTarget`,
        // passing along any ETH attached to this function call to cover protocol fees.
        (bool success,) = swapTarget.call{value: msg.value}(swapCallData);
        require(success, 'SWAP_CALL_FAILED');
        // Refund any unspent protocol fees to the sender.
        payable(msg.sender).transfer(address(this).balance);

        // Use our current buyToken balance to determine how much we've bought.
        boughtAmount = buyToken.balanceOf(address(this)) - boughtAmount;
        console.log("boughtAmount After=",boughtAmount);
        console.log("sellTokenAmount After=",sellToken.balanceOf(address(this)));
        emit BoughtTokens(sellToken, buyToken, boughtAmount);
    }

    function batchSwap(QuoteResponse[] calldata list) external onlyOwner payable{
        for(uint i=0;i<list.length;i++){
            swap(list[i].sellToken,list[i].buyToken,list[i].spender,list[i].swapTarget,list[i].sellAmount,list[i].swapCallData);
        }
    }
}

